def fitness(cv, jobOffer):
    prompt = f"Tell me if this resume is fitting the job offer. You should only answer with 'Fit', 'Partially Fit', or 'Not Fit':\n\"\"\"\n Resume: {cv}\n Job Offer: {jobOffer}\n\"\"\""
    result = ollama.generate(model='gemma3:1b', prompt=prompt)
    return result['response']


